result:
#landing-feature {
  background-color: #2a7ab2;
  padding: 145px 0 100px;
}
#landing-sections {
  font-family: 'Avenir';
  background-color: #fff;
  color: #313131;
}

.text-lg {
  font-size: 24px !important;
}
.text-md {
  font-size: 20px !important;
}
.text-sm {
  font-size: 16px !important;
  margin-top: 20px;;
}
@media (min-width: 480px){
  .text-lg {
    font-size: 36px !important;
  }
  .text-md {
    font-size: 26px !important;
  }
  .text-sm {
    font-size: 18px !important;
    margin-top: 20px;;
  }
}
@media (min-width: 768px) {
  .text-lg {
    font-size: 48px !important;
  }
  .text-md {
    font-size: 30px !important;
  }
  .text-sm {
    font-size: 20px !important;
    margin-top: 20px;;
  }
}
@media (min-width: 992px) {
  .text-lg {
    font-size: 60px !important;
  }
  .text-md {
    font-size: 36px !important;
  }
  .text-sm {
    font-size: 24px !important;
    margin-top: 20px;;
  }
}

.bg-image {
  width: 100%;
  margin-top: -50px;
}
@media (min-width: 768px) {
  .bg-image {
    width: 100%;
    margin-top: -150px;
  }
}

.text-link {
  padding-top: 40px !important;
}
.text-link a {
  color: #313131;
  line-height: 1.6;
  display: block;
  width: fit-content;
  border-bottom: 3px solid #0084e1;
}
.text-link a:hover {
  cursor: pointer;
  color: #0084e1;
  text-decoration: none;
}
.text-link a::after {
  font-family: "Font Awesome 5 Free";
  font-weight: 900;
  content: "\f061";
  color: #313131;
  margin-left: 8px;
  display: inline-block;
  font-size: 13px;
}
.text-link a:hover::after {
  color: #0084e1;
}
.text-heavy {
  font-weight: 600 !important;
}

.text-btn {
  width: fit-content;
  border-bottom: 4px solid rgba(255, 65, 76, .8);
}

.text-btn:hover {
  cursor: pointer;
  width: fit-content;
}

.feature-section {
  padding: 0 0 75px;
}
.feature-section:first-child {
  padding-top: 75px;
}

.record-items {
  padding: 60px;
  text-align: center;
}
.record-items a {
  color: #313131;
  font-size: 32px;
  font-weight: 900;
}

.record-items:hover {
  cursor: pointer;
  background-color: #0084e1;
}
.record-items:hover a {
  color: #fff;
}

.landing-text-top {
  font-family: brandon-grotesque, sans-serif;
  font-size: 16px;
  color: #fff;
  font-weight: 600;
  padding-bottom: 20px;
}
.landing-text-top span {
  width: 100px;
  border-top: 3px solid rgb(255, 255, 255);
  display: inline-block;
  margin: 0 0 4px 4px;
}

.landing-text {
  font-family: brandon-grotesque, sans-serif;
  font-size: 32px;
  color: #fff;
  font-weight: 600;
  padding-bottom: 20px;
  text-transform: uppercase;
}
.nav-items {
  font-family: brandon-grotesque, sans-serif;
  font-size: 14px;
  padding: 8px 0 22px;
  color: #fff;
  font-weight: 600;
}
.nav-items li {
  display: inline;
  list-style: none;
}
.nav-items li::after {
  content: '';
  border-right: 1px solid #fff;
  width: 100%;
  margin: 0 12px;
}
.nav-items li:last-child::after {
  border-right: none;
}
.nav-items a {
  color: #fff;
  opacity: .95;
}
.nav-items a:last-child {
  border-right: none;
}
.nav-items a:hover {
  opacity: .5;
  cursor: pointer;
}

.alert-items {
  color: #fff;
}

.img-container {
  display: block;
  text-align: center;
  width: fit-content;
  margin: 0 auto;
}
.play-button-outer {
  margin: 20px auto 0;
  width: 60px;
  height: 60px;
  background-color: #f5f5f5;
  cursor: pointer;
  border-radius: 50%;
}

.play-button {
  margin: 0 auto;
  top: 21px;
  position: relative;
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 10px 0 10px 20px;
  border-color: transparent transparent transparent #000;
  left: 3px;
}

.play-button-outer:hover {
  opacity: .75;
  cursor: pointer;
}

.title-text {
  font-size: 30px;
  font-family: 'Avenir';
  color: #313131;
  line-height: 1.4;
  margin-bottom: .5rem;
}
.subtitle-text {
  font-size: 20px;
  font-family: 'Avenir';
  color: #313131;
  line-height: 1.6;
}

.feat-items {
  font-family: 'Avenir';
}
.feat-items p {
  display: block;
  margin-bottom: 20px;
  margin-top: 20px;
  font-size: 20px;
  line-height: 1.6;
  color: #313131;
}
.feat-items a {
  color: #0084e1;
}
.feat-items a:hover {
  cursor: pointer;
  color: #0077cc;
  text-decoration: underline;
}

heavy {
  font-weight: 700;
}

.sec-title {

  color: #313131;
  font-size: 20px;
  line-height: 1.6;
  font-weight: 700;
  padding-bottom: 15px;
}
.sec-description {
  color: #313131;
  font-size: 20px;
  line-height: 1.6;
  font-weight: 400;
  margin-bottom: 15px;
}
.sec-link {
  padding-top: 15px;
  position: relative;
}
.sec-link a {
  color: #0084e1;
  font-size: 20px;
  line-height: 1.6;
  display: block;
  width: fit-content;
}
.sec-link a:hover {
  cursor: pointer;
  color: #0077cc;
  text-decoration: underline;
}

.section-separator {
  border-top: 1px solid rgba(0, 0, 0, .05);
  padding: 5rem 0;
}
