  border-bottom: 3px solid #f9676b;
}
.text-link a:hover {
  cursor: pointer;
  color: #f9676b;
  text-decoration: none;
}
.text-link a::after {
  font-family: "Font Awesome 5 Free";
  font-weight: 900;
  content: "\f061";
  color: #313131;
  margin-left: 8px;
  display: inline-block;
  font-size: 13px;
}
.text-link a:hover::after {
  color: #f9676b;
}
