#benno-title {
  font-family: Gibson, sans-serif;
  color: #000;
  background-color: #fff;
  padding: 100px 0;
  font-weight: 400;
  letter-spacing: .85px;
  position: relative;
}
#benno-filters {
  font-family: Gibson, sans-serif;
  color: #000;
  background-color: #fff;
  padding: 0 0 25px;
  font-weight: 400;
  letter-spacing: .85px;
  position: relative;
}
#benno-body {
  font-family: Gibson, sans-serif;
  color: #000;
  background-color: #fff;
  padding: 50px 0 100px;
  font-weight: 400;
  letter-spacing: .85px;
  position: relative;
}

.benno-textbox {
  max-width: 600px;
  padding-right: 50px;
}
.benno-textbox h1 {
  font-size: 32px;
  color: rgba(0,0,0,.85);
  margin-bottom: 4px;
  font-weight: 500;
  letter-spacing: .1px;
}
.benno-textbox p {
  font-size: 24px;
  color: rgba(0,0,0,.5);
  line-height: 1.35;
  letter-spacing: .1px;
}
.benno-textbox h2 {
  font-size: 18px;
  color: #c3c3c3;
  line-height: 1.35;
  margin-top: 40px;
}

.benno-filterbox {
  padding-bottom: 10px;
  border-bottom: 1px solid rgba(0,0,0,.65);
}

.benno-filterbox a {
  font-size: 15px;
  color: rgba(0,0,0,.65);
  line-height: 1.35;
  letter-spacing: .1px;
  margin-right: 20px;
  text-transform: uppercase;
}

.benno-filterbox a:hover {
  color: rgba(0,0,0,.85);
  cursor: pointer;
  text-decoration: none;
}

.logo-box {
  font-family: brandon-grotesque, sans-serif;
  width: fit-content;
  margin-bottom: 20px;
}
.logo-box .spec {
  color: #000;
  font-weight: 600;
  height: 100%;
  display: flex;
  font-size: 40px;
  letter-spacing: .85px;
  border: 2px solid #0077cc;
  padding: 10px 14px;
  cursor: default;
  color: #fff;
  background-color: #0077cc;
  box-shadow: -10px 10px 33px -7px #929292, 2px 2px 0 0 #f1f1f1;
}
.logo-box .spec:hover {
  cursor: default !important;
}

.red {
  color: #ed1c24 !important;
}
.red:hover {
  border-color: #ca1a21 !important;
  color: #ca1a21 !important;
}
.blue {
  border-color: #0077cc !important;
  color: #0077cc !important;
}
.blue:hover {
  color: #006ab5 !important;
  border-color: #006ab5 !important;
  text-decoration: none;
}
.link-btn {
  padding-left: 5px;
}
.link-btn:hover {
  cursor: pointer;
}

.spec.dark {
  color: #fff;
  background-color: #000;
}

.sample-text {
  font-size: 13px;
  font-weight: 600;
  color: #000;
  text-align: left;
  padding: 8px 20px;
  margin-bottom: 20px;
  border-top: 1px solid rgba(0,0,0,.1);
  border-bottom: 2px solid rgba(0,0,0,1);
}
.sample-search {
font-size: 20px;
font-weight: 600;
color: #000;
text-align: left;
padding: 16px 20px;
margin-bottom: 20px;
max-width: 600px;
margin: 0 auto;
border: 1px solid #000;
border-radius: 40px;
}

.searchbar-box {
  border-radius: 3px;
  font-size: 20px;
  padding: 6px 0;
  background-color: #f7f7f7;
}
.searchbar-icon {
  margin-right: 10px;
  color: #000;
}
.searchbar-input {
  width: calc(100% - 30px);
  border: none;
  font-weight: 600;
  color: #000;
  background-color: #f7f7f7;
}
.searchbar-input::placeholder {
  font-weight: 400;
  color: #000;
}
.searchbar-input:focus {
  outline: none;
}
.text-link a {
  border-color: #2a7ab2 !important;
}
.text-link a:hover {
  color: #2a7ab2 !important;
}

.text-link a:hover::after {
  color: #2a7ab2 !important;
}

.inline-link a {
  border-color: #2a7ab2 !important;
}
.inline-link a:hover {
  color: #2a7ab2 !important;
}
.inline-link a:hover::after {
  color: #2a7ab2 !important;
}

.record-text-top-cat::after {
  background: repeating-linear-gradient(60deg, #2a7ab2, #2a7ab2 1px, #fff 1px, #fff 1.25px) !important;
}

.featured-items::before {
  background: repeating-linear-gradient(60deg, #2a7ab2, #2a7ab2 1px, #fff 1px, #fff 1.25px);
  top: 0;
}

.crazy-border {
  background: repeating-linear-gradient(60deg, #2a7ab2, #2a7ab2 1px, #fff 1px, #fff 1.25px) !important;
}

.record-items:hover .icon-link::after {
  color: #2a7ab2;
}

@media (min-width: 992px) {
  .featured-items {
    width: calc(100% - 250px) !important;
    margin-left: 250px !important;
  }
}
@media (min-width: 1200px) {
  .featured-items {
    width: calc(100% - 500px) !important;
    margin-left: 250px !important;
  }
}
